#include <iostream>
#include <vector>
#include <queue>
#include <cmath>
#include <algorithm>

using namespace std;

const int MAXN = 1e5 + 5;
const int MAXM = 1e5 + 5;
const int MAXP = 1e5 + 5;

int N, M, S, T;
vector<int> G[MAXN];
int dist[MAXN];
bool vis[MAXN];
int prime[MAXP];

bool isPrime(int x) {
    if (x <= 1) return false;
    for (int i = 2; i * i <= x; i++) {
        if (x % i == 0) return false;
    }
    return true;
}

void bfs() {
    queue<int> q;
    q.push(S);
    dist[S] = 0;
    vis[S] = true;
    while (!q.empty()) {
        int u = q.front();
        q.pop();
        for (int v : G[u]) {
            if (!vis[v]) {
                vis[v] = true;
                dist[v] = dist[u] + 1;
                q.push(v);
            }
        }
    }
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    cin >> N >> M >> S >> T;
    S--; T--;
    for (int i = 0; i < M; i++) {
        int u, v;
        cin >> u >> v;
        u--; v--;
        G[u].push_back(v);
        G[v].push_back(u);
    }

    bfs();

    if (!vis[T]) {
        cout << -1 << endl;
        return 0;
    }

    int d = dist[T];
    for (int i = 2; i <= d; i++) {
        if (isPrime(i)) {
            if (i == d) {
                cout << i << endl;
                return 0;
            }
            break;
        }
    }
    cout << -1 << endl;

    return 0;
}
