import java.io.*;
import java.util.*;

public class Main {
    static final int N = 100005;
    static int n, m, s, t;
    static int[] head = new int[N];
    static int[] to = new int[N];
    static int[] nxt = new int[N];
    static int[] vis = new int[N];
    static int[] dis = new int[N];
    static int[] prime = new int[N];
    static int cnt = 0;
    static boolean[] isPrime = new boolean[N];

    static void add(int u, int v) {
        to[++cnt] = v;
        nxt[cnt] = head[u];
        head[u] = cnt;
    }

    static boolean check(int x) {
        if (x <= 1) return false;
        for (int i = 2; i * i <= x; i++) {
            if (x % i == 0) return false;
        }
        return true;
    }

    static void dfs(int u, int d) {
        if (u == t) {
            dis[d] = 1;
            return;
        }
        vis[u] = 1;
        for (int i = head[u]; i != 0; i = nxt[i]) {
            int v = to[i];
            if (vis[v] == 0) {
                dfs(v, d + 1);
                if (dis[d + 1] == 1) {
                    dis[d] = 1;
                    return;
                }
            }
        }
        vis[u] = 0;
    }

    public static void main(String[] args) throws IOException {
        Scanner sc = new Scanner(System.in);
        n = sc.nextInt();
        m = sc.nextInt();
        s = sc.nextInt();
        t = sc.nextInt();
        for (int i = 2; i <= n; i++) {
            isPrime[i] = true;
        }
        for (int i = 2; i <= n; i++) {
            for (int j = 2; j * i <= n; j++) {
                isPrime[i * j] = false;
            }
        }
        for (int i = 1; i <= m; i++) {
            int u = sc.nextInt();
            int v = sc.nextInt();
            add(u, v);
            add(v, u);
        }
        for (int i = 1; i <= n; i++) {
            Arrays.fill(vis, 0);
        }
        for (int i = 1; i <= n; i++) {
            dis[i] = 0;
        }
        for (int i = 1; i <= n; i++) {
            if (dis[i] == 0) {
                dfs(i, 0);
            }
        }
        for (int i = 1; i <= n; i++) {
            if (dis[i] == 1) {
                for (int j = 2; j <= n; j++) {
                    if (dis[j] == 1) {
                        int d = i + j;
                        if (check(d)) {
                            System.out.println(d);
                            return;
                        }
                    }
                }
            }
        }
        System.out.println(-1);
    }
}

