#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
    int n, k;
    cin >> n >> k;

    vector<int> a(n);
    for (int i = 0; i < n; i++) {
        cin >> a[i];
    }

    vector<int> b(n);
    for (int i = 0; i < n; i++) {
        b[i] = a[i];
    }

    sort(b.begin(), b.end());

    int cnt = 0;
    for (int i = 0; i < n; i++) {
        if (a[i] != b[i]) {
            cnt++;
        }
    }

    if (cnt <= k) {
        for (int i = 0; i < n; i++) {
            cout << a[i] << endl;
        }
    } else {
        int i = 0;
        while (i < n && cnt > k) {
            if (a[i] != b[i]) {
                swap(a[i], a[i + 1]);
                cnt--;
                i++;
            }
            i++;
        }
        for (int i = 0; i < n; i++) {
            cout << a[i] << endl;
        }
    }

    return 0;
}
