import sys

def rough_sorting(n, k, arr):
    inv = 0
    for i in range(n - 1):
        if arr[i] > arr[i + 1]:
            inv += 1
    if inv <= k:
        return arr

    swaps = 0
    for i in range(n - 1):
        if arr[i] > arr[i + 1]:
            swaps += 1
            if swaps <= k:
                arr[i], arr[i + 1] = arr[i + 1], arr[i]
            else:
                break

    inv = 0
    for i in range(n - 1):
        if arr[i] > arr[i + 1]:
            inv += 1
    if inv > k:
        for i in range(n - 1, -1, -1):
            if arr[i] < arr[i - 1]:
                arr[i], arr[i - 1] = arr[i - 1], arr[i]
                inv -= 1
                if inv == k:
                    break

    return arr

def main():
    input = sys.stdin.readline
    n, k = map(int, input().split())
    arr = [0] * n
    for i in range(n):
        arr[i] = int(input())
    arr = rough_sorting(n, k, arr)
    for i in range(n):
        print(arr[i])

if __name__ == "__main__":
    main()
