#include <stdio.h>
#include <string.h>

int max(int a, int b) {
    return (a > b) ? a : b;
}

int count(char *s) {
    int count = 0;
    int i;
    for (i = 0; s[i] != '\0'; i++) {
        if (s[i] == 'B') {
            count++;
        }
    }
    return count;
}

int main() {
    int L;
    char s[101];
    int max_capture = 0;

    scanf("%d %s", &L, s);

    for (int i = 0; i < L; i++) {
        if (s[i] == '.') {
            int capture = 0;
            int j;
            for (j = i - 1; j >= 0; j--) {
                if (s[j] == 'W') {
                    break;
                }
                if (s[j] == 'B') {
                    capture++;
                }
            }
            for (j = i + 1; j < L; j++) {
                if (s[j] == 'W') {
                    break;
                }
                if (s[j] == 'B') {
                    capture++;
                }
            }
            max_capture = max(max_capture, capture);
        }
    }

    printf("%d\n", max_capture);

    return 0;
}
