#include <iostream>
#include <string>
#include <algorithm>

int main() {
    int L;
    std::string S;
    std::cin >> L >> S;

    int maxCaptured = 0;

    for (int i = 0; i < L; i++) {
        if (S[i] == '.') {
            int count = 0;
            int j = i - 1;
            while (j >= 0 && S[j] == 'B') {
                count++;
                j--;
            }
            j = i + 1;
            while (j < L && S[j] == 'B') {
                count++;
                j++;
            }
            maxCaptured = std::max(maxCaptured, count);
        }
    }

    for (int i = 0; i < L - 1; i++) {
        if (S[i] == 'W' && S[i + 1] == 'B') {
            int count = 0;
            int j = i - 1;
            while (j >= 0 && S[j] == 'B') {
                count++;
                j--;
            }
            j = i + 2;
            while (j < L && S[j] == 'B') {
                count++;
                j++;
            }
            maxCaptured = std::max(maxCaptured, count);
        }
        if (S[i] == 'B' && S[i + 1] == 'W') {
            int count = 0;
            int j = i - 1;
            while (j >= 0 && S[j] == 'W') {
                count++;
                j--;
            }
            j = i + 2;
            while (j < L && S[j] == 'W') {
                count++;
                j++;
            }
            maxCaptured = std::max(maxCaptured, count);
        }
    }

    std::cout << maxCaptured << std::endl;

    return 0;
}
