import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int L = scanner.nextInt();
        String S = scanner.next();

        int[] left = new int[L];
        int[] right = new int[L];

        for (int i = 0; i < L; i++) {
            if (S.charAt(i) == 'B') {
                left[i] = i == 0 ? 0 : left[i - 1] + 1;
                right[i] = i == L - 1 ? 0 : right[i + 1] + 1;
            } else {
                left[i] = i == 0 ? 0 : left[i - 1];
                right[i] = i == L - 1 ? 0 : right[i + 1];
            }
        }

        int maxCaptured = 0;
        for (int i = 0; i < L; i++) {
            if (S.charAt(i) == '.') {
                int captured = 0;
                int j = i - 1;
                while (j >= 0 && S.charAt(j) == 'B') {
                    captured += 1;
                    j = left[j] - 1;
                }
                j = i + 1;
                while (j < L && S.charAt(j) == 'B') {
                    captured += 1;
                    j = right[j] + 1;
                }
                maxCaptured = Math.max(maxCaptured, captured);
            }
        }

        System.out.println(maxCaptured);
    }
}
