def max_capture(s):
    n = len(s)
    max_capture = 0

    for i in range(n):
        if s[i] == '.':
            left_capture = 0
            right_capture = 0
            left_chain = False
            right_chain = False

            for j in range(i - 1, -1, -1):
                if s[j] == '.':
                    break
                if s[j] == 'B':
                    left_chain = True
                else:
                    left_chain = False
                if left_chain:
                    left_capture += 1

            for j in range(i + 1, n):
                if s[j] == '.':
                    break
                if s[j] == 'B':
                    right_chain = True
                else:
                    right_chain = False
                if right_chain:
                    right_capture += 1

            max_capture = max(max_capture, left_capture + right_capture)

    return max_capture

s = input().split()[1]
print(max_capture(s))
