import sys
input = sys.stdin.readline

class SegmentTree:
    def __init__(self, n):
        self.n = n
        self.tree = [0] * (4 * n)

    def update(self, node, start, end, index, value):
        if start == end:
            self.tree[node] += value
            return
        mid = (start + end) // 2
        if index <= mid:
            self.update(2 * node, start, mid, index, value)
        else:
            self.update(2 * node + 1, mid + 1, end, index, value)
        self.tree[node] = self.tree[2 * node] + self.tree[2 * node + 1]

    def query(self, node, start, end, left, right):
        if start > right or end < left:
            return 0
        if start >= left and end <= right:
            return self.tree[node]
        mid = (start + end) // 2
        return self.query(2 * node, start, mid, left, right) + self.query(2 * node + 1, mid + 1, end, left, right)

def solve():
    n, q, s, t = map(int, input().split())
    A = [0] + list(map(int, input().split()))
    seg = SegmentTree(n + 1)
    for i in range(1, n + 1):
        seg.update(1, 1, n, i, A[i] - A[i - 1])
    ans = 0
    for _ in range(q):
        l, r, x = map(int, input().split())
        if x >= 0:
            seg.update(1, 1, n, l, x)
            seg.update(1, 1, n, r + 1, -x)
        else:
            seg.update(1, 1, n, l, -x)
            seg.update(1, 1, n, r + 1, x)
        ans += t * seg.query(1, 1, n, 1, n) - s * seg.query(1, 1, n, 1, n - 1)
        print(ans)

if __name__ == "__main__":
    t = 1
    for _ in range(t):
        solve()
