import java.util.*;
import java.io.*;

public class Main {
    static long gcd(long a, long b) {
        if (b == 0) return a;
        return gcd(b, a % b);
    }

    static long lcm(long a, long b) {
        return a * b / gcd(a, b);
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        int n = Integer.parseInt(br.readLine());
        long[] a = new long[n];
        for (int i = 0; i < n; i++) {
            a[i] = Long.parseLong(br.readLine());
        }
        long[] b = new long[n];
        for (int i = 0; i < n; i++) {
            b[i] = a[i];
        }
        Arrays.sort(b);
        long ans = 0;
        for (int i = 0; i < n; i++) {
            long g = b[0];
            for (int j = 1; j < n; j++) {
                g = gcd(g, b[j]);
            }
            long sum = 0;
            for (int j = 0; j < n; j++) {
                if (a[j] % g == 0) sum += a[j];
            }
            ans = Math.max(ans, sum);
            for (int j = 0; j < n; j++) {
                a[j] /= g;
            }
        }
        System.out.println(ans);
    }
}
