#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <math.h>

#define N 200005

typedef struct {
    long long l, r;
} Interval;

int cmp(const void *a, const void *b) {
    Interval *x = (Interval *)a;
    Interval *y = (Interval *)b;
    return x->l - y->l;
}

int main() {
    int n;
    long long L;
    scanf("%d%lld", &n, &L);

    Interval *intervals = (Interval *)malloc(n * sizeof(Interval));
    for (int i = 0; i < n; i++) {
        scanf("%lld%lld", &intervals[i].l, &intervals[i].r);
    }

    qsort(intervals, n, sizeof(Interval), cmp);

    long long *prefix = (long long *)malloc(n * sizeof(long long));
    prefix[0] = intervals[0].l;
    for (int i = 1; i < n; i++) {
        prefix[i] = prefix[i - 1] + intervals[i].l - intervals[i - 1].r;
    }

    long long x = 0, y = 0;
    for (int i = 0; i < n; i++) {
        long long l = 0, r = n;
        while (l < r) {
            long long mid = (l + r) / 2;
            long long sum = 0;
            for (int j = 0; j <= mid; j++) {
                if (j == 0) {
                    sum += intervals[j].l;
                } else {
                    sum += intervals[j].l - intervals[j - 1].r;
                }
            }
            if (sum >= L) {
                r = mid;
            } else {
                l = mid + 1;
            }
        }
        if (l == n) {
            y = n;
            break;
        }
        if (prefix[l - 1] >= L) {
            x = l;
            break;
        }
    }

    printf("%lld %lld\n", x, y);

    free(prefix);
    free(intervals);

    return 0;
}
