#include <iostream>
#include <vector>
#include <algorithm>
#include <utility>

using namespace std;

struct Interval {
    long long l, r;
};

bool compareIntervals(const Interval& a, const Interval& b) {
    return a.r < b.r;
}

int main() {
    int n;
    long long L;
    cin >> n >> L;

    vector<Interval> intervals(n);
    for (int i = 0; i < n; i++) {
        cin >> intervals[i].l >> intervals[i].r;
    }

    sort(intervals.begin(), intervals.end(), compareIntervals);

    vector<long long> dp(n + 1, 0);
    for (int i = 0; i < n; i++) {
        dp[i + 1] = dp[i] + intervals[i].r - intervals[i].l;
    }

    long long x = 0, y = 0;
    for (int i = 0; i <= n; i++) {
        if (dp[i] >= L) {
            x = i;
            break;
        }
    }

    for (int i = 0; i <= n; i++) {
        for (int j = i + 1; j <= n; j++) {
            long long len = dp[j] - dp[i];
            if (len >= L) {
                y = max(y, j - i);
                break;
            }
        }
    }

    cout << x << " " << y << endl;

    return 0;
}
