import java.io.*;
import java.util.*;

public class Main {
    static class Interval implements Comparable<Interval> {
        long l, r;

        public Interval(long l, long r) {
            this.l = l;
            this.r = r;
        }

        @Override
        public int compareTo(Interval o) {
            return Long.compare(l, o.l);
        }
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        StringTokenizer st = new StringTokenizer(br.readLine());
        int n = Integer.parseInt(st.nextToken());
        long L = Long.parseLong(st.nextToken());
        Interval[] intervals = new Interval[n];
        for (int i = 0; i < n; i++) {
            st = new StringTokenizer(br.readLine());
            intervals[i] = new Interval(Long.parseLong(st.nextToken()), Long.parseLong(st.nextToken()));
        }
        Arrays.sort(intervals);
        long[] dp = new long[n + 1];
        long[] pre = new long[n + 1];
        for (int i = 1; i <= n; i++) {
            pre[i] = pre[i - 1] + intervals[i - 1].r - intervals[i - 1].l;
            dp[i] = dp[i - 1] + (intervals[i - 1].r - intervals[i - 1].l);
        }
        long x = 0, y = 0;
        for (int i = 1; i <= n; i++) {
            if (pre[i] >= L) {
                x = i;
                break;
            }
        }
        for (int i = n; i >= 1; i--) {
            if (pre[i] >= L) {
                y = i;
                break;
            }
        }
        System.out.println(x + " " + y);
    }
}
