#include <stdio.h>
#include <stdlib.h>

#define N 205

int cmp(const void *a, const void *b) {
    return *(int *)a - *(int *)b;
}

int main() {
    int n, p[N], q[N], pos[N], vis[N], ans = 0;
    scanf("%d", &n);
    for (int i = 1; i <= n; i++) scanf("%d", &p[i]);
    for (int i = 1; i <= n; i++) scanf("%d", &q[i]);
    for (int i = 1; i <= n; i++) pos[p[i]] = i;
    for (int i = 1; i <= n; i++) vis[i] = 0;
    qsort(pos + 1, n, sizeof(int), cmp);
    for (int i = 1; i <= n; i++) {
        if (pos[i] != i) {
            int j = pos[i];
            while (j != i) {
                vis[j] = 1;
                j = pos[j];
            }
            ans = 1;
        }
    }
    for (int i = 1; i <= n; i++) {
        if (vis[i]) {
            int j = i;
            while (j <= n && vis[j]) j++;
            for (int k = i; k < j; k++) {
                if (q[k] > q[k + 1]) {
                    ans = -1;
                    break;
                }
            }
            if (ans == -1) break;
        }
    }
    printf("%d\n", ans);
    return 0;
}
