#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
    int n;
    cin >> n;
    vector<int> p(n), q(n);
    for (int i = 0; i < n; i++) {
        cin >> p[i];
    }
    for (int i = 0; i < n; i++) {
        cin >> q[i];
    }

    vector<int> pos(n + 1);
    for (int i = 0; i < n; i++) {
        pos[p[i]] = i;
    }

    vector<int> order(n);
    for (int i = 0; i < n; i++) {
        order[i] = pos[q[i]];
    }

    vector<int> vis(n + 1);
    int ans = 0;
    for (int i = 0; i < n; i++) {
        if (vis[i] == 0) {
            int j = i;
            while (vis[j] == 0) {
                vis[j] = 1;
                j = order[j];
                ans++;
            }
        }
    }

    if (ans == n) {
        cout << 0 << endl;
    } else {
        int d = 0;
        while (true) {
            bool ok = true;
            for (int i = 0; i < n; i++) {
                if (p[i] != i + 1) {
                    ok = false;
                    break;
                }
            }
            if (ok) {
                cout << d << endl;
                break;
            }
            d++;
            for (int i = 0; i < n; i++) {
                if (p[i] != i + 1) {
                    int j = i;
                    while (p[j] != j + 1) {
                        int temp = p[j];
                        p[j] = p[order[j]];
                        p[order[j]] = temp;
                        j = order[j];
                    }
                }
            }
        }
    }

    return 0;
}
