import java.io.*;
import java.util.*;

public class Main {
    static class FastReader {
        BufferedReader br;
        StringTokenizer st;

        public FastReader() {
            br = new BufferedReader(new InputStreamReader(System.in));
        }

        String next() {
            while (st == null || !st.hasMoreElements()) {
                try {
                    st = new StringTokenizer(br.readLine());
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
            return st.nextToken();
        }

        int nextInt() {
            return Integer.parseInt(next());
        }

        long nextLong() {
            return Long.parseLong(next());
        }
    }

    static class Pair implements Comparable<Pair> {
        int x;
        int y;

        public Pair(int x, int y) {
            this.x = x;
            this.y = y;
        }

        @Override
        public int compareTo(Pair o) {
            return Integer.compare(x, o.x);
        }
    }

    public static void main(String[] args) {
        FastReader sc = new FastReader();
        int n = sc.nextInt();
        int[] p = new int[n + 1];
        int[] q = new int[n + 1];
        for (int i = 1; i <= n; i++) {
            p[i] = sc.nextInt();
        }
        for (int i = 1; i <= n; i++) {
            q[i] = sc.nextInt();
        }
        int[] pos = new int[n + 1];
        for (int i = 1; i <= n; i++) {
            pos[p[i]] = i;
        }
        int[] vis = new int[n + 1];
        int[] d = new int[n + 1];
        for (int i = 1; i <= n; i++) {
            d[i] = (int) 1e18;
        }
        for (int i = 1; i <= n; i++) {
            if (vis[i] == 0) {
                int j = i;
                while (vis[j] == 0) {
                    vis[j] = 1;
                    d[j] = 0;
                    j = pos[q[j]];
                }
                int cnt = 0;
                while (vis[j] == 1) {
                    cnt++;
                    j = pos[q[j]];
                }
                d[i] = cnt;
            }
        }
        int[] dp = new int[n + 1];
        for (int i = 1; i <= n; i++) {
            dp[i] = d[i];
        }
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j < i; j++) {
                if (dp[i] > dp[j] + (i - j - 1)) {
                    dp[i] = dp[j] + (i - j - 1);
                }
            }
        }
        int ans = (int) 1e18;
        for (int i = 1; i <= n; i++) {
            if (dp[i] < ans) {
                ans = dp[i];
            }
        }
        if (ans == (int) 1e18) {
            System.out.println(-1);
        } else {
            System.out.println(ans);
        }
    }
}
