import sys
from collections import deque

def solve():
    n = int(input())
    p = list(map(int, input().split()))
    q = list(map(int, input().split()))

    pos = [[] for _ in range(n + 1)]
    for i in range(n):
        pos[p[i]].append(i)

    d = 0
    for i in range(1, n + 1):
        if pos[i]:
            q_pos = pos[i].index(min(pos[i]))
            if q_pos != 0:
                d = max(d, q_pos)

    if d == 0:
        print(0)
        return

    q_deq = deque()
    for i in range(n):
        if p[i] != q[i]:
            q_deq.append(i)

    while q_deq:
        i = q_deq.popleft()
        if p[i] != q[i]:
            j = pos[p[i]].index(i)
            pos[p[i]].pop(j)
            pos[q[i]].append(i)
            while pos[q[i]]:
                k = pos[q[i]].index(min(pos[q[i]]))
                if q[k] != q[i]:
                    q_deq.append(pos[q[i]].pop(k))
            d += 1

    if d == 0:
        print(-1)
    else:
        print(d)

if __name__ == "__main__":
    solve()
