#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAXN 3005

int n, m, k;
int a, b, c;
int t;
int s[MAXN];
int dp[MAXN];

int main() {
    scanf("%d %d %d", &n, &m, &k);
    scanf("%d %d %d", &a, &b, &c);
    scanf("%d", &t);
    for (int i = 1; i <= m; i++) {
        scanf("%d", &s[i]);
    }
    s[m + 1] = n;
    for (int i = 1; i <= m; i++) {
        dp[i] = (s[i] - s[i - 1]) * b;
    }
    for (int i = 1; i <= m; i++) {
        for (int j = 1; j <= i; j++) {
            int d = (s[i] - s[j - 1]) * c;
            if (dp[j - 1] + d <= t) {
                dp[i] = dp[i] > dp[j - 1] + d ? dp[i] : dp[j - 1] + d;
            }
        }
    }
    int ans = 0;
    for (int i = 1; i <= m; i++) {
        int d = (s[i] - s[i - 1]) * c;
        if (dp[i] + d <= t) {
            ans = i;
        }
    }
    int sum = 0;
    for (int i = 1; i <= ans; i++) {
        sum += (s[i] - s[i - 1]) - 1;
    }
    printf("%d\n", sum + 1);
    return 0;
}
