#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAXN 100005

typedef struct {
    int to;
    int next;
} Edge;

typedef struct {
    int head[100005];
    int tot;
    Edge e[MAXN * 2];
} Graph;

void addEdge(Graph *g, int u, int v) {
    g->e[++g->tot] = (Edge) {v, g->head[u]};
    g->head[u] = g->tot;
}

int n, m;
int in[MAXN];
int out[MAXN];
int vis[MAXN];
int cur[MAXN];
Graph g;

void dfs(int u) {
    vis[u] = 1;
    for (int i = g.head[u]; i; i = g.e[i].next) {
        int v = g.e[i].to;
        if (!vis[v]) {
            dfs(v);
        } else if (cur[v]) {
            in[u] = 1;
            out[v] = 1;
        }
    }
    cur[u] = 1;
}

int main() {
    scanf("%d%d", &n, &m);
    for (int i = 1; i <= m; i++) {
        int u, v, w;
        scanf("%d%d%d", &u, &v, &w);
        addEdge(&g, u, v);
        addEdge(&g, v, u);
        if (w > 1) {
            addEdge(&g, v, u);
            addEdge(&g, u, v);
        }
    }
    for (int i = 1; i <= n; i++) {
        if (!vis[i]) {
            dfs(i);
        }
    }
    for (int i = 1; i <= n; i++) {
        if (in[i] && out[i]) {
            printf("No\n");
            return 0;
        }
    }
    printf("Yes\n");
    return 0;
}
