#include <iostream>
#include <vector>
#include <queue>
#include <unordered_map>

using namespace std;

const int MAX_N = 100000;

struct Edge {
    int to;
    int weight;
};

int main() {
    int N, M;
    cin >> N >> M;

    vector<vector<Edge>> graph(N + 1);
    vector<int> in_degree(N + 1, 0);

    for (int i = 0; i < M; i++) {
        int A, B, x;
        cin >> A >> B >> x;
        graph[A].push_back({B, x});
        graph[B].push_back({A, -x});
        in_degree[A]++;
        in_degree[B]++;
    }

    queue<int> q;
    for (int i = 1; i <= N; i++) {
        if (in_degree[i] == 0) {
            q.push(i);
        }
    }

    unordered_map<int, int> parent;
    while (!q.empty()) {
        int u = q.front();
        q.pop();
        for (const auto& e : graph[u]) {
            int v = e.to;
            int w = e.weight;
            in_degree[v]--;
            if (in_degree[v] == 0) {
                q.push(v);
            }
            if (w > 0) {
                parent[v] = u;
            }
        }
    }

    for (int i = 1; i <= N; i++) {
        if (in_degree[i] != 0) {
            cout << "No" << endl;
            return 0;
        }
    }

    for (int i = 1; i <= N; i++) {
        if (parent[i].first == i) {
            cout << "Yes" << endl;
            return 0;
        }
    }

    cout << "No" << endl;
    return 0;
}
