import java.io.*;
import java.util.*;

public class Main {
    static class Edge {
        int to, next, x;
        public Edge(int to, int next, int x) {
            this.to = to;
            this.next = next;
            this.x = x;
        }
    }

    static int N, M;
    static int[] head = new int[100010];
    static Edge[] e = new Edge[200010];
    static int tot = 0;
    static int[] vis = new int[100010];
    static int[] dep = new int[100010];
    static int[] dfn = new int[100010];
    static int idx = 0;
    static int[] low = new int[100010];
    static int[] stk = new int[100010];
    static int top = 0;
    static int[] scc = new int[100010];
    static int cnt = 0;
    static int[] in = new int[100010];
    static boolean[] flag = new boolean[100010];

    static void add(int u, int v, int w) {
        e[++tot] = new Edge(v, head[u], w);
        head[u] = tot;
    }

    static void tarjan(int u) {
        dfn[u] = low[u] = ++idx;
        stk[++top] = u;
        for (int i = head[u]; i != 0; i = e[i].next) {
            int v = e[i].to;
            if (dfn[v] == 0) {
                tarjan(v);
                low[u] = Math.min(low[u], low[v]);
            } else if (scc[v] == 0) {
                low[u] = Math.min(low[u], dfn[v]);
            }
        }
        if (dfn[u] == low[u]) {
            ++cnt;
            while (stk[top] != u) {
                scc[stk[top]] = cnt;
                --top;
            }
            scc[u] = cnt;
            --top;
        }
    }

    static void dfs(int u, int d) {
        if (vis[u] != 0) {
            if (dep[u] != d) {
                flag[scc[u]] = true;
            }
            return;
        }
        vis[u] = 1;
        dep[u] = d;
        for (int i = head[u]; i != 0; i = e[i].next) {
            int v = e[i].to;
            dfs(v, d + e[i].x);
        }
        vis[u] = 0;
    }

    public static void main(String[] args) throws IOException {
        Scanner sc = new Scanner(System.in);
        N = sc.nextInt();
        M = sc.nextInt();
        for (int i = 1; i <= M; i++) {
            int u = sc.nextInt();
            int v = sc.nextInt();
            int w = sc.nextInt();
            add(u, v, -w);
            add(v, u, w);
        }
        for (int i = 1; i <= N; i++) {
            if (dfn[i] == 0) {
                tarjan(i);
            }
        }
        for (int i = 1; i <= cnt; i++) {
            in[i] = 0;
            flag[i] = false;
        }
        for (int i = 1; i <= N; i++) {
            dfs(i, 0);
        }
        for (int i = 1; i <= M; i++) {
            int u = sc.nextInt();
            int v = sc.nextInt();
            int w = sc.nextInt();
            if (scc[u] != scc[v]) {
                if (flag[scc[u]] || flag[scc[v]]) {
                    System.out.println("No");
                    return;
                }
            } else {
                if (in[scc[u]] != 0 && in[scc[u]] != w) {
                    System.out.println("No");
                    return;
                }
                in[scc[u]] = w;
            }
        }
        System.out.println("Yes");
    }
}
