import sys
from collections import defaultdict

def dfs(node, visited, graph, parent):
    visited[node] = True
    for neighbor in graph[node]:
        if not visited[neighbor]:
            parent[neighbor] = node
            dfs(neighbor, visited, graph, parent)
        elif parent[node] != neighbor:
            cycle = []
            while node != neighbor:
                cycle.append(node)
                node = parent[node]
            cycle.append(neighbor)
            cycle.reverse()
            return cycle

def detect_bug(n, m, rules):
    graph = defaultdict(list)
    for a, b, x in rules:
        graph[a].append(b)
        graph[b].append(a)

    visited = [False] * (n + 1)
    parent = [None] * (n + 1)

    for i in range(1, n + 1):
        if not visited[i]:
            cycle = dfs(i, visited, graph, parent)
            if len(cycle) > 2:
                return cycle

    return None

def main():
    input = sys.stdin.readline
    n, m = map(int, input().split())
    rules = []
    for _ in range(m):
        a, b, x = map(int, input().split())
        rules.append((a, b, x))

    cycle = detect_bug(n, m, rules)
    if cycle is None:
        print("Yes")
    else:
        print("No")

if __name__ == "__main__":
    main()
