#include <iostream>
#include <vector>
#include <string>
#include <algorithm>
#include <cstring>

using namespace std;

const int N = 2005;
const int M = 1e9 + 7;

int h, w, mod;
int dp[N][N][2];
int f[N][N][2];
int g[N][N][2];
int a[N][N];

int add(int x, int y) {
    return (x + y >= mod) ? x + y - mod : x + y;
}

int mul(int x, int y) {
    return (1LL * x * y) % mod;
}

int main() {
    cin >> h >> w >> mod;
    for (int i = 1; i <= h; i++) {
        for (int j = 1; j <= w; j++) {
            cin >> a[i][j];
        }
    }

    memset(dp, 0, sizeof(dp));
    memset(f, 0, sizeof(f));
    memset(g, 0, sizeof(g));

    for (int i = 1; i <= h; i++) {
        for (int j = 1; j <= w; j++) {
            if (i == 1 && j == 1) {
                f[i][j][0] = a[i][j] - '0';
                g[i][j][0] = a[i][j] - '0';
            } else if (i == 1) {
                f[i][j][0] = g[i][j - 1][0];
                g[i][j][0] = (a[i][j] == '*') ? f[i][j - 1][0] : 0;
            } else if (j == 1) {
                f[i][j][0] = g[i - 1][j][0];
                g[i][j][0] = (a[i][j] == '*') ? f[i - 1][j][0] : 0;
            } else {
                f[i][j][0] = add(g[i - 1][j][0], g[i][j - 1][0]);
                g[i][j][0] = (a[i][j] == '*') ? mul(f[i - 1][j][0], f[i][j - 1][0]) : 0;
            }
        }
    }

    for (int i = 1; i <= h; i++) {
        for (int j = 1; j <= w; j++) {
            if (i == 1 && j == 1) {
                dp[i][j][1] = f[i][j][0];
            } else if (i == 1) {
                dp[i][j][1] = add(dp[i][j - 1][1], f[i][j][0]);
            } else if (j == 1) {
                dp[i][j][1] = add(dp[i - 1][j][1], f[i][j][0]);
            } else {
                dp[i][j][1] = add(add(dp[i - 1][j][1], dp[i][j - 1][1]), f[i][j][0]);
            }
        }
    }

    int ans = 0;
    for (int i = 1; i <= h; i++) {
        for (int j = 1; j <= w; j++) {
            if (i == h && j == w) {
                ans = add(ans, dp[i][j][1]);
            } else if (i == h) {
                ans = add(ans, dp[i][j][1]);
            } else if (j == w) {
                ans = add(ans, dp[i][j][1]);
            } else {
                ans = add(ans, dp[i][j][1]);
            }
        }
    }

    cout << ans << endl;

    return 0;
}
