import sys
input = sys.stdin.readline

def add(a, b):
    return (a + b) % mod

def mul(a, b):
    return (a * b) % mod

def dfs(i, j, k, dp):
    if i == h - 1 and j == w - 1:
        return dp[i][j][k]
    if dp[i][j][k] != -1:
        return dp[i][j][k]
    res = 0
    if k == 0:
        if j + 1 < w and grid[i][j + 1].isdigit():
            res = add(res, dfs(i, j + 1, 0, dp))
        if i + 1 < h and grid[i + 1][j].isdigit():
            res = add(res, dfs(i + 1, j, 0, dp))
    else:
        if j + 1 < w and grid[i][j + 1] == '*':
            res = add(res, mul(dfs(i, j + 1, 1, dp), dfs(i, j + 1, 0, dp)))
        if i + 1 < h and grid[i + 1][j] == '*':
            res = add(res, mul(dfs(i + 1, j, 1, dp), dfs(i + 1, j, 0, dp)))
        if j + 1 < w and grid[i][j + 1].isdigit():
            res = add(res, mul(dfs(i, j + 1, 0, dp), dfs(i, j + 1, 1, dp)))
        if i + 1 < h and grid[i + 1][j].isdigit():
            res = add(res, mul(dfs(i + 1, j, 0, dp), dfs(i + 1, j, 1, dp)))
    dp[i][j][k] = res
    return res

h, w, mod = map(int, input().split())
grid = [list(input().rstrip()) for _ in range(h)]
dp = [[[-1] * 2 for _ in range(w)] for _ in range(h)]
print(dfs(0, 0, 0, dp))
