#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAXN 100005

int n, m;
long long a[MAXN], b[MAXN];
long long d[MAXN];

int main() {
    scanf("%d %d", &n, &m);
    for (int i = 1; i <= n; i++) {
        scanf("%lld", &a[i]);
    }
    for (int i = 1; i <= m; i++) {
        scanf("%lld", &b[i]);
    }
    long long q;
    scanf("%lld", &q);
    for (int i = 1; i <= q; i++) {
        scanf("%lld", &d[i]);
    }
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= m; j++) {
            a[i] = a[i] * b[j];
        }
    }
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= m; j++) {
            int k = i + (j - 1) * n;
            int len = 0;
            long long x = a[k];
            while (x > 0) {
                x /= 10;
                len++;
            }
            if (d[i] <= len) {
                printf("%lld", (a[k] / (long long)pow(10, d[i] - 1)) % 10);
            } else {
                printf("x");
            }
        }
        printf("\n");
    }
    return 0;
}
