#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

using namespace std;

int main() {
    int N, M;
    cin >> N >> M;

    vector<int> a(N);
    for (int i = 0; i < N; i++) {
        cin >> a[i];
    }

    vector<int> b(M);
    for (int i = 0; i < M; i++) {
        cin >> b[i];
    }

    int Q;
    cin >> Q;

    vector<long long> d(Q);
    for (int i = 0; i < Q; i++) {
        cin >> d[i];
    }

    vector<int> ans(Q);
    for (int i = 0; i < Q; i++) {
        long long x = d[i];
        int j = 0;
        while (x > 0) {
            x /= 10;
            j++;
        }
        int row = (j - 1) / M;
        int col = (j - 1) % M;
        if (row >= N || col >= M) {
            ans[i] = 'x';
        } else {
            int num = a[row] * b[col];
            int k = j - 1;
            while (num > 0) {
                ans[i] = num % 10 + '0';
                num /= 10;
                k--;
                if (k < 0) break;
            }
        }
    }

    for (int i = 0; i < Q; i++) {
        cout << ans[i];
    }
    cout << endl;

    return 0;
}
