import java.io.*;
import java.util.*;

public class Main {
    static class FastReader {
        BufferedReader br;
        StringTokenizer st;

        public FastReader() {
            br = new BufferedReader(new InputStreamReader(System.in));
        }

        String next() {
            while (st == null || !st.hasMoreElements()) {
                try {
                    st = new StringTokenizer(br.readLine());
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
            return st.nextToken();
        }

        int nextInt() {
            return Integer.parseInt(next());
        }

        long nextLong() {
            return Long.parseLong(next());
        }

        double nextDouble() {
            return Double.parseDouble(next());
        }

        String nextLine() {
            String str = "";
            try {
                str = br.readLine();
            } catch (IOException e) {
                e.printStackTrace();
            }
            return str;
        }
    }

    public static void main(String[] args) {
        FastReader sc = new FastReader();
        PrintWriter out = new PrintWriter(System.out);
        int n = sc.nextInt();
        int m = sc.nextInt();
        long[] a = new long[n + 1];
        long[] b = new long[m + 1];
        for (int i = 1; i <= n; i++) {
            a[i] = sc.nextLong();
        }
        for (int i = 1; i <= m; i++) {
            b[i] = sc.nextLong();
        }
        int q = sc.nextInt();
        long[] d = new long[q + 1];
        for (int i = 1; i <= q; i++) {
            d[i] = sc.nextLong();
        }
        long[][] c = new long[n + 1][m + 1];
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= m; j++) {
                c[i][j] = a[i] * b[j];
            }
        }
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= m; j++) {
                c[i][j] += c[i - 1][j] + c[i][j - 1] - c[i - 1][j - 1];
            }
        }
        StringBuilder sb = new StringBuilder();
        for (int i = 1; i <= q; i++) {
            long x = d[i];
            int idx = 0;
            while (x > 0) {
                x /= 10;
                idx++;
            }
            if (idx > c[n][m]) {
                sb.append('x');
            } else {
                int r = (int) (n * m - idx + 1);
                int c = (int) (idx - 1);
                int x1 = (int) (c % m);
                int y1 = (int) (c / m);
                int x2 = (int) (r % m);
                int y2 = (int) (r / m);
                long ans = c[y2 + 1][x2 + 1] - c[y2 + 1][x1] - c[y1][x2 + 1] + c[y1][x1];
                ans += c[y1][x1] - 1;
                sb.append((ans % 10));
            }
        }
        out.println(sb.toString());
        out.close();
    }
}
