import math
import sys

def solve():
    n, m = map(int, input().split())
    a = list(map(int, input().split()))
    b = list(map(int, input().split()))
    q = int(input())
    d = list(map(int, input().split()))

    for i in range(n):
        a[i] = str(a[i])
    for j in range(m):
        b[j] = str(b[j])

    for i in range(n):
        for j in range(m):
            a[i] += str(a[i][0] * b[j])

    for i in range(1, n):
        for j in range(m):
            a[i] += str(a[i-1][j])

    ans = []
    for k in range(q):
        index = d[k]
        if index > len(a[0]) * n:
            ans.append('x')
        else:
            row = (index - 1) // len(a[0])
            col = (index - 1) % len(a[0])
            ans.append(a[row][col])

    print(''.join(ans))

if __name__ == "__main__":
    solve()
