#include <stdio.h>
#include <stdlib.h>
#include <math.h>

typedef struct {
    double x, v;
} human;

int compare(const void *a, const void *b) {
    human *h1 = (human *)a;
    human *h2 = (human *)b;
    return (h1->x - h2->x) * (h1->v > 0 ? 1 : -1) - (h2->x - h1->x) * (h2->v > 0 ? 1 : -1);
}

int main() {
    int N;
    scanf("%d", &N);
    human zombie;
    scanf("%lf %lf", &zombie.x, &zombie.v);
    human humans[N];
    for (int i = 0; i < N; i++) {
        scanf("%lf %lf", &humans[i].x, &humans[i].v);
    }
    qsort(humans, N, sizeof(human), compare);
    for (int i = 0; i < N; i++) {
        human *h = &humans[i];
        if (h->v * zombie.v >= 0) {
            printf("-1\n");
            continue;
        }
        double t = (h->x - zombie.x) / (zombie.v - h->v);
        if (t <= 0) {
            printf("%f\n", h->x / h->v);
        } else {
            printf("%f\n", t);
        }
    }
    return 0;
}
