#include <iostream>
#include <vector>
#include <algorithm>
#include <cmath>

struct Human {
    double x, v;
};

bool compareHumans(const Human& a, const Human& b) {
    return a.v > b.v;
}

int main() {
    int N;
    std::cin >> N;

    double xZ, vZ;
    std::cin >> xZ >> vZ;

    std::vector<Human> humans(N);
    for (int i = 0; i < N; i++) {
        std::cin >> humans[i].x >> humans[i].v;
    }

    std::sort(humans.begin(), humans.end(), compareHumans);

    std::vector<double> times(N, -1);
    int i = 0;
    double t = (humans[i].x - xZ) / std::max(humans[i].v, vZ);
    if (t >= 0) {
        times[i] = t;
    }

    for (int j = 1; j < N; j++) {
        while (i < j && (humans[i].v * (t - (humans[i].x - xZ) / std::max(humans[i].v, vZ)) + vZ * t) <= humans[j].v * t) {
            i++;
            if (i < N) {
                t = (humans[i].x - xZ) / std::max(humans[i].v, vZ);
                if (t >= 0) {
                    times[i] = t;
                }
            }
        }
        if (humans[j].v * t - vZ * t - (humans[j].x - xZ) / std::max(humans[j].v, vZ) >= 0) {
            times[j] = (humans[j].v * t - vZ * t - (humans[j].x - xZ) / std::max(humans[j].v, vZ)) / (humans[j].v - vZ);
        }
    }

    for (int i = 0; i < N; i++) {
        std::cout << times[i] << std::endl;
    }

    return 0;
}
