import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.io.PrintWriter;
import java.util.Arrays;
import java.util.StringTokenizer;

public class Main {
    static class Pair implements Comparable<Pair> {
        double x, v;

        public Pair(double x, double v) {
            this.x = x;
            this.v = v;
        }

        @Override
        public int compareTo(Pair o) {
            return Double.compare(x / v, o.x / o.v);
        }
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        StringTokenizer st = new StringTokenizer(br.readLine());

        int n = Integer.parseInt(st.nextToken());
        double xz = Double.parseDouble(st.nextToken());
        double vz = Double.parseDouble(st.nextToken());

        Pair[] a = new Pair[n + 1];
        a[0] = new Pair(xz, vz);
        for (int i = 1; i <= n; i++) {
            st = new StringTokenizer(br.readLine());
            a[i] = new Pair(Double.parseDouble(st.nextToken()), Double.parseDouble(st.nextToken()));
        }

        Arrays.sort(a, 1, n + 1);

        for (int i = 1; i <= n; i++) {
            double t = (a[i].x - a[0].x) / (a[0].v - a[i].v);
            if (a[0].v == a[i].v) {
                if (a[0].x > a[i].x) {
                    pw.println(-1);
                } else {
                    pw.println((a[i].x - a[0].x) / Math.abs(a[i].v));
                }
            } else if (t < 0) {
                pw.println(-1);
            } else {
                pw.println(a[0].x + a[0].v * t);
            }
        }

        pw.close();
    }
}
