#include <iostream>
#include <vector>
#include <set>
#include <algorithm>

using namespace std;

int main() {
    int n, m;
    cin >> n >> m;

    vector<pair<int, int>> rooks(m);
    for (int i = 0; i < m; i++) {
        cin >> rooks[i].first >> rooks[i].second;
    }

    set<pair<int, int>> rows, cols;
    for (int i = 0; i < m; i++) {
        rows.insert(rooks[i]);
        cols.insert(rooks[i]);
    }

    int minCaptured = m - 1;
    int maxCaptured = m;

    for (int i = 0; i < m; i++) {
        if (rows.find(rooks[i]) != rows.end()) {
            rows.erase(rooks[i]);
            maxCaptured--;
        }
        if (cols.find(rooks[i]) != cols.end()) {
            cols.erase(rooks[i]);
            maxCaptured--;
        }
    }

    cout << minCaptured << " " << maxCaptured << endl;

    return 0;
}
