def rooks_game():
    n, m = map(int, input().split())
    rooks = []
    for _ in range(m):
        x, y = map(int, input().split())
        rooks.append((x, y))

    # Check if all rooks are in the same position
    if len(set(rooks)) == 1:
        print(m, m)
        return

    # Check if there are any rooks that can capture all other rooks
    for i in range(m):
        x, y = rooks[i]
        captured = 0
        for j in range(m):
            if i != j:
                if x == rooks[j][0] or y == rooks[j][1]:
                    captured += 1
        if captured == m - 1:
            print(m, m)
            return

    # Check if there are any rooks that can capture all other rooks in a row or column
    for i in range(m):
        x, y = rooks[i]
        captured = 0
        for j in range(m):
            if i != j:
                if x == rooks[j][0]:
                    captured += 1
                elif y == rooks[j][1]:
                    captured += 1
        if captured == m - 1:
            print(m, m)
            return

    # If no rook can capture all other rooks, the minimum and maximum number of captured rooks is m - 1
    print(m - 1, m)

rooks_game()
