#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAX_N 505
#define MAX_L 200005

int n, m, k, l;
char bomb[MAX_N][MAX_N];
char move[MAX_L];
int x, y;
int cnt[MAX_N][MAX_N];
int ans;

int main() {
    scanf("%d %d %d %d", &n, &m, &k, &l);
    for (int i = 1; i <= n; i++) {
        scanf("%s", bomb[i] + 1);
    }
    scanf("%s", move + 1);
    for (int i = 1; i <= l; i++) {
        cnt[x][y]++;
        int dx[] = {0, 0, -1, 1};
        int dy[] = {-1, 1, 0, 0};
        int dir = move[i] - 'L';
        x += dx[dir];
        y += dy[dir];
        if (x < 1 || x > m - n + 1 || y < 1 || y > m - n + 1) {
            x -= dx[dir];
            y -= dy[dir];
            continue;
        }
        for (int j = 1; j <= n; j++) {
            if (bomb[j][x - j + 1] == 'X') {
                cnt[x][y]++;
            }
        }
    }
    for (int i = 1; i <= m - n + 1; i++) {
        for (int j = 1; j <= m - n + 1; j++) {
            if (cnt[i][j] >= k) {
                ans++;
            }
        }
    }
    printf("%d\n", ans);
    return 0;
}
