import java.util.*;
import java.io.*;

public class Main {
    static int N, M, K, L;
    static int[][] B;
    static int[][] damage;
    static String S;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        StringTokenizer st = new StringTokenizer(br.readLine());
        N = Integer.parseInt(st.nextToken());
        M = Integer.parseInt(st.nextToken());
        K = Integer.parseInt(st.nextToken());
        L = Integer.parseInt(st.nextToken());
        B = new int[N][N];
        for (int i = 0; i < N; i++) {
            String line = br.readLine();
            for (int j = 0; j < N; j++) {
                B[i][j] = line.charAt(j) == 'X' ? 1 : 0;
            }
        }
        S = br.readLine();
        damage = new int[M - N + 1][M - N + 1];
        int x = 1, y = 1;
        for (int i = 0; i < L; i++) {
            char c = S.charAt(i);
            switch (c) {
                case 'U':
                    y = Math.max(y - 1, 1);
                    break;
                case 'D':
                    y = Math.min(y + 1, M - N + 1);
                    break;
                case 'L':
                    x = Math.max(x - 1, 1);
                    break;
                case 'R':
                    x = Math.min(x + 1, M - N + 1);
                    break;
            }
            for (int j = 0; j < N; j++) {
                for (int k = 0; k < N; k++) {
                    if (B[j][k] == 1) {
                        damage[x + j - 1][y + k - 1]++;
                    }
                }
            }
        }
        int count = 0;
        for (int i = 0; i < M - N + 1; i++) {
            for (int j = 0; j < M - N + 1; j++) {
                if (damage[i][j] >= K) {
                    count++;
                }
            }
        }
        System.out.println(count);
    }
}
