from collections import defaultdict

def solve():
    N, M, K, L = map(int, input().split())
    B = [input() for _ in range(N)]
    S = input()

    damage = defaultdict(int)
    x, y = 1, 1
    for _ in range(L):
        for i in range(N):
            for j in range(N):
                if B[i][j] == 'X':
                    damage[x + i - 1, y + j - 1] += 1
        if S[_] == 'U':
            y -= 1
        elif S[_] == 'D':
            y += 1
        elif S[_] == 'L':
            x -= 1
        else:
            x += 1
        if x < 1 or x > M - N + 1 or y < 1 or y > M - N + 1:
            break

    ans = 0
    for x in range(1, M - N + 2):
        for y in range(1, M - N + 2):
            if damage[x, y] >= K:
                ans += 1
    print(ans)

solve()
