#include <stdio.h>
#include <stdlib.h>

int max(int a, int b) {
    return (a > b) ? a : b;
}

int min(int a, int b) {
    return (a < b) ? a : b;
}

int main() {
    int n;
    scanf("%d", &n);

    int* a = (int*)malloc(n * sizeof(int));
    for (int i = 0; i < n; i++) {
        scanf("%d", &a[i]);
    }

    int even = 0, odd = 0;
    for (int i = 0; i < n; i++) {
        if (a[i] % 2 == 0) {
            even++;
        } else {
            odd++;
        }
    }

    if (even == 0 || odd == 0) {
        printf("0\n");
    } else {
        int ans = 0;
        while (even > 1 && odd > 1) {
            ans++;
            int sum = a[even - 2] + a[odd - 2];
            if (sum % 2 == 0) {
                even++;
            } else {
                odd++;
            }
            a[even - 2] = sum;
            even--;
            odd--;
        }
        printf("%d\n", ans);
    }

    free(a);
    return 0;
}
