#include <iostream>
#include <vector>
#include <algorithm>

int main() {
    int n;
    std::cin >> n;

    std::vector<int> a(n);
    for (int i = 0; i < n; ++i) {
        std::cin >> a[i];
    }

    int even = 0, odd = 0;
    for (int i = 0; i < n; ++i) {
        if (a[i] % 2 == 0) {
            even++;
        } else {
            odd++;
        }
    }

    if (even == 0 || odd == 0) {
        std::cout << 0 << std::endl;
        return 0;
    }

    int ans = 0;
    while (even > 1 && odd > 1) {
        ans++;
        even--;
        odd--;
        int sum = a[even] + a[odd];
        a.push_back(sum);
        if (sum % 2 == 0) {
            even++;
        } else {
            odd++;
        }
    }

    std::cout << ans << std::endl;
    return 0;
}
