import java.util.*;
import java.io.*;

public class Main {
    static long[] a;
    static long n;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        n = Long.parseLong(br.readLine());
        a = new long[(int)n];
        String[] input = br.readLine().split(" ");
        for (int i = 0; i < n; i++) {
            a[i] = Long.parseLong(input[i]);
        }
        Arrays.sort(a);
        long even = 0, odd = 0;
        for (int i = 0; i < n; i++) {
            if (a[i] % 2 == 0) even++;
            else odd++;
        }
        if (even == 0 || odd == 0) System.out.println(0);
        else {
            long ans = 0;
            long i = 0, j = (int)n - 1;
            while (i < j) {
                if (a[i] % 2 == 0 && a[j] % 2 == 0) {
                    i++;
                    j--;
                } else if (a[i] % 2 == 1 && a[j] % 2 == 1) {
                    i++;
                    j--;
                } else {
                    long sum = a[i] + a[j];
                    a[(int)j] = sum;
                    ans++;
                    i++;
                    j--;
                }
            }
            System.out.println(ans);
        }
    }
}
