#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <math.h>

#define N 200005
#define M 1000000005

typedef struct {
    int q, x, s, t;
} Query;

int n, q, l, r;
int a[N], b[N], c[N];
Query q[N];

void add(int i, int x) {
    for (; i <= n; i += i & -i) {
        c[i] += x;
    }
}

int sum(int i) {
    int s = 0;
    for (; i; i -= i & -i) {
        s += c[i];
    }
    return s;
}

int main() {
    scanf("%d%d%d%d", &n, &q, &l, &r);
    for (int i = 1; i <= n; i++) {
        scanf("%d", &a[i]);
        b[i] = a[i];
    }
    for (int i = 1; i <= q; i++) {
        scanf("%d%d%d%d", &q[i].q, &q[i].x, &q[i].s, &q[i].t);
    }
    for (int i = 1; i <= q; i++) {
        if (q[i].q == 1) {
            int x = q[i].x;
            int s = q[i].s;
            int t = q[i].t;
            for (int j = 1; j <= n; j++) {
                if (b[j] >= x) {
                    b[j] = (b[j] + s) * t;
                }
            }
        } else {
            int x = q[i].x;
            int s = q[i].s;
            int t = q[i].t;
            for (int j = 1; j <= n; j++) {
                if (b[j] <= x) {
                    b[j] = (int) (b[j] - s) / t;
                }
            }
        }
        for (int j = 1; j <= n; j++) {
            add(j, 1);
        }
        for (int j = 1; j <= n; j++) {
            a[j] = sum(j);
        }
    }
    int ans = 0;
    for (int i = 1; i <= n; i++) {
        if (l <= b[i] && b[i] <= r) {
            ans++;
        }
    }
    printf("%d\n", ans);
    return 0;
}
