import sys
input = sys.stdin.readline

class SegmentTree:
    def __init__(self, n):
        self.n = n
        self.tree = [0] * (4 * n)

    def update(self, node, start, end, index, value):
        if start == end:
            self.tree[node] = value
            return
        mid = (start + end) // 2
        if index <= mid:
            self.update(2 * node, start, mid, index, value)
        else:
            self.update(2 * node + 1, mid + 1, end, index, value)
        self.tree[node] = max(self.tree[2 * node], self.tree[2 * node + 1])

    def query(self, node, start, end, left, right):
        if start > right or end < left:
            return 0
        if start >= left and end <= right:
            return self.tree[node]
        mid = (start + end) // 2
        return max(self.query(2 * node, start, mid, left, right), self.query(2 * node + 1, mid + 1, end, left, right))

def solve():
    n, q, l, r = map(int, input().split())
    a = list(map(int, input().split()))
    seg = SegmentTree(n)
    for i in range(n):
        seg.update(1, 0, n - 1, i, a[i])
    for _ in range(q):
        q, x, s, t = map(int, input().split())
        if q == 1:
            seg.update(1, 0, n - 1, 0, (t * x + s) if x <= seg.query(1, 0, n - 1, 0, n - 1) else float('inf'))
        else:
            seg.update(1, 0, n - 1, 0, (x - s) // t if x >= seg.query(1, 0, n - 1, 0, n - 1) else -float('inf'))
    print(sum(1 for i in range(n) if l <= seg.query(1, 0, n - 1, i, i) <= r))

t = 1
for _ in range(t):
    solve()
