#include <stdio.h>
#include <stdlib.h>

typedef struct {
    int l, r;
} Yokan;

int cmpYokan(const void *a, const void *b) {
    Yokan *x = (Yokan *)a;
    Yokan *y = (Yokan *)b;
    return x->l - y->l;
}

int main() {
    int N, M;
    scanf("%d %d", &N, &M);
    Yokan *yokans = (Yokan *)malloc(N * sizeof(Yokan));
    for (int i = 0; i < N; i++) {
        scanf("%d %d", &yokans[i].l, &yokans[i].r);
    }
    qsort(yokans, N, sizeof(Yokan), cmpYokan);
    int dist = 0;
    int pos = 0;
    int i = 0;
    while (i < N) {
        if (yokans[i].l > pos) {
            dist += yokans[i].l - pos;
            pos = yokans[i].l;
            while (i < N && yokans[i].l <= pos) {
                dist += yokans[i].r - yokans[i].l;
                i++;
            }
        } else {
            dist += M - pos;
            pos = M;
            while (i < N && yokans[i].r >= pos) {
                dist += yokans[i].r - yokans[i].l;
                i++;
            }
        }
        dist += M;
        pos = 0;
    }
    printf("%d\n", dist);
    free(yokans);
    return 0;
}
