#include <iostream>
#include <vector>
#include <algorithm>

struct Yokan {
    int l, r;
};

bool compareYokans(const Yokan& a, const Yokan& b) {
    return a.r < b.r;
}

int main() {
    int n, m;
    std::cin >> n >> m;

    std::vector<Yokan> yokans(n);
    for (int i = 0; i < n; ++i) {
        std::cin >> yokans[i].l >> yokans[i].r;
    }

    std::sort(yokans.begin(), yokans.end(), compareYokans);

    int l = 0, r = 0, dist = 0;
    for (int i = 0; i < n; ++i) {
        if (yokans[i].l >= r) {
            dist += yokans[i].l - r;
            r = yokans[i].r;
        } else if (yokans[i].r <= l) {
            dist += yokans[i].r - l;
            l = yokans[i].l;
        } else {
            dist += yokans[i].r - l;
            l = yokans[i].l;
            r = yokans[i].r;
        }
    }

    std::cout << dist << std::endl;

    return 0;
}
