import java.util.Arrays;
import java.util.Comparator;
import java.util.Scanner;

class Yokan {
    int left, right;

    public Yokan(int left, int right) {
        this.left = left;
        this.right = right;
    }
}

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        int m = scanner.nextInt();
        Yokan[] yokans = new Yokan[n];
        for (int i = 0; i < n; i++) {
            yokans[i] = new Yokan(scanner.nextInt(), scanner.nextInt());
        }
        Arrays.sort(yokans, Comparator.comparingInt(y -> y.left));
        int distance = 0;
        int left = 0;
        int right = m;
        for (int i = 0; i < n; i++) {
            if (yokans[i].left >= left) {
                distance += yokans[i].left - left;
                left = yokans[i].left;
            } else if (yokans[i].right <= right) {
                distance += right - yokans[i].right;
                right = yokans[i].right;
            } else {
                distance += Math.min(yokans[i].left - left, right - yokans[i].right) * 2;
                if (yokans[i].left - left < right - yokans[i].right) {
                    left = yokans[i].left;
                    right = Math.min(right, yokans[i].right);
                } else {
                    right = yokans[i].right;
                    left = Math.max(left, yokans[i].left);
                }
            }
        }
        if (left < right) {
            distance += right - left;
        }
        System.out.println(distance);
    }
}
