import sys

def solve():
    n, m = map(int, input().split())
    intervals = []
    for _ in range(n):
        l, r = map(int, input().split())
        intervals.append((l, r))

    intervals.sort(key=lambda x: x[0])

    ans = 0
    i = 0
    while i < n:
        if intervals[i][0] <= m:
            if intervals[i][1] <= m:
                ans += intervals[i][1] - intervals[i][0]
                i += 1
            else:
                ans += 2 * m - intervals[i][0]
                i += 1
        else:
            if intervals[i][1] <= m:
                ans += 2 * (m - intervals[i][0])
                i += 1
            else:
                ans += 2 * (intervals[i][1] - m)
                i += 1

    for i in range(n - 1, -1, -1):
        if intervals[i][1] <= m:
            break
        else:
            ans += 2 * (intervals[i][1] - m)

    print(ans)

if __name__ == "__main__":
    solve()
