#include <stdio.h>
#include <stdlib.h>
#include <math.h>
#include <string.h>
#include <limits.h>

#define N 2000
#define INF 1000000000

int max(int a, int b) { return a > b ? a : b; }
int min(int a, int b) { return a < b ? a : b; }

int main() {
    int h, w, a[N][N], b[N][N], c[N][N], d[N][N], e[N][N], f[N][N];
    int i, j, k, l, m, n, x, y, z, ans;

    scanf("%d%d", &h, &w);
    for (i = 0; i < h; i++) {
        for (j = 0; j < w; j++) {
            scanf("%d", &a[i][j]);
        }
    }

    for (i = 0; i < h; i++) {
        for (j = 0; j < w; j++) {
            b[i][j] = a[i][j];
        }
    }

    for (j = 0; j < w; j++) {
        for (i = 0; i < h; i++) {
            c[i][j] = b[i][j];
        }
    }

    for (i = 0; i < h; i++) {
        for (j = 0; j < w; j++) {
            d[i][j] = max(c[i][j], c[i][(j + 1) % w]);
        }
    }

    for (j = 0; j < w; j++) {
        for (i = 0; i < h; i++) {
            e[i][j] = max(d[i][j], d[(i + 1) % h][j]);
        }
    }

    for (i = 0; i < h; i++) {
        for (j = 0; j < w; j++) {
            f[i][j] = max(e[i][j], e[(i + 1) % h][j]);
        }
    }

    ans = INF;
    for (i = 0; i < h; i++) {
        for (j = 0; j < w; j++) {
            x = 0;
            y = 0;
            for (k = 0; k < h; k++) {
                z = 0;
                for (l = 0; l < w; l++) {
                    if (f[k][l] == f[i][j]) {
                        z++;
                    }
                }
                x = max(x, z);
            }
            z = 0;
            for (k = 0; k < w; k++) {
                if (f[i][k] == f[i][j]) {
                    z++;
                }
            }
            y = max(y, z);
            ans = min(ans, max(f[i][j] - a[i][j], f[i][j] - a[0][0]));
            ans = min(ans, max(f[i][j] - a[0][0], f[i][j] - a[h - 1][w - 1]));
            ans = min(ans, max(f[i][j] - a[h - 1][w - 1], f[i][j] - a[0][0]));
            ans = min(ans, max(f[i][j] - a[i][0], f[i][j] - a[i][w - 1]));
            ans = min(ans, max(f[i][j] - a[i][w - 1], f[i][j] - a[i][0]));
            ans = min(ans, max(f[i][j] - a[0][j], f[i][j] - a[h - 1][j]));
            ans = min(ans, max(f[i][j] - a[h - 1][j], f[i][j] - a[0][j]));
            ans = min(ans, max(f[i][j] - a[i][j], f[i][j] - a[0][0]));
            ans = min(ans, max(f[i][j] - a[0][0], f[i][j] - a[i][j]));
            ans = min(ans, max(f[i][j] - a[i][j], f[i][j] - a[h - 1][w - 1]));
            ans = min(ans, max(f[i][j] - a[h - 1][w - 1], f[i][j] - a[i][j]));
            ans = min(ans, max(f[i][j] - a[i][j], f[i][j] - a[i][0]));
            ans = min(ans, max(f[i][j] - a[i][0], f[i][j] - a[i][j]));
            ans = min(ans, max(f[i][j] - a[i][j], f[i][j] - a[0][j]));
            ans = min(ans, max(f[i][j] - a[0][j], f[i][j] - a[i][j]));
        }
    }

    printf("%d\n", ans);

    return 0;
}
