#include <stdlib.h>

int main() {
    int n, x, a, o;
    scanf("%d %d %d %d", &n, &x, &a, &o);

    int mask = (1 << n) - 1;
    int* s = (int*)malloc((1 << n) * sizeof(int));

    int i, j;
    for (i = 0; i < (1 << n); i++) {
        s[i] = i;
    }

    for (i = 0; i < (1 << n); i++) {
        for (j = 0; j < (1 << n); j++) {
            if (((s[i] ^ s[j]) == x) || ((s[i] & s[j]) == a) || ((s[i] | s[j]) == o)) {
                s[j] = -1;
            }
        }
    }

    int count = 0;
    for (i = 0; i < (1 << n); i++) {
        if (s[i] != -1) {
            count++;
        }
    }

    if (count == (1 << n)) {
        printf("Yes\n");
        for (i = 0; i < (1 << n); i++) {
            if (s[i] != -1) {
                printf("%d ", s[i]);
            }
        }
        printf("\n");
    } else {
        printf("No\n");
    }

    free(s);
    return 0;
}
