using namespace std;

int main() {
    int N, X, A, O;
    cin >> N >> X >> A >> O;

    vector<int> S;
    for (int i = 0; i < (1 << N); i++) {
        bool valid = true;
        for (int j = 0; j < N; j++) {
            if ((i & (1 << j)) && (i & (1 << j)) && (i & (1 << j)) && (X & (1 << j))) {
                valid = false;
                break;
            }
            if ((i & (1 << j)) && (i & (1 << j)) && (i & (1 << j)) && (A & (1 << j))) {
                valid = false;
                break;
            }
            if ((i & (1 << j)) && (i & (1 << j)) && (i & (1 << j)) && (O & (1 << j))) {
                valid = false;
                break;
            }
            if (!(i & (1 << j)) && !(i & (1 << j)) && !(i & (1 << j)) && (X & (1 << j))) {
                valid = false;
                break;
            }
            if (!(i & (1 << j)) && !(i & (1 << j)) && !(i & (1 << j)) && (A & (1 << j))) {
                valid = false;
                break;
            }
            if (!(i & (1 << j)) && !(i & (1 << j)) && !(i & (1 << j)) && (O & (1 << j))) {
                valid = false;
                break;
            }
        }
        if (valid) {
            S.push_back(i);
        }
    }

    if (S.size() == (1 << N)) {
        cout << "Yes" << endl;
        for (int i : S) {
            cout << i << " ";
        }
        cout << endl;
    } else {
        cout << "No" << endl;
    }

    return 0;
}
