import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        int x = scanner.nextInt();
        int a = scanner.nextInt();
        int o = scanner.nextInt();
        scanner.close();

        int[] result = solve(n, x, a, o);
        if (result == null) {
            System.out.println("No");
        } else {
            System.out.println("Yes");
            for (int i = 0; i < (1 << n); i++) {
                System.out.print(result[i] + " ");
            }
            System.out.println();
        }
    }

    public static int[] solve(int n, int x, int a, int o) {
        int[] result = new int[1 << n];
        for (int i = 0; i < (1 << n); i++) {
            result[i] = -1;
        }

        for (int i = 0; i < (1 << n); i++) {
            if (result[i] == -1) {
                int j = i;
                while (j < (1 << n)) {
                    if (result[j] == -1) {
                        if ((i ^ j) != x && (i & j) != a && (i | j) != o) {
                            result[j] = i;
                        }
                    }
                    j = j + (1 << (n - 1));
                }
            }
        }

        for (int i = 0; i < (1 << n); i++) {
            if (result[i] == -1) {
                return null;
            }
        }

        return result;
    }
}
