#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAXN 60
#define MAXK 60
#define MAXA 50

int min(int a, int b) {
    return a < b ? a : b;
}

int max(int a, int b) {
    return a > b ? a : b;
}

int dp[MAXN + 1][MAXK + 1][MAXK + 1];

int main() {
    int n, k, a[MAXN], r[MAXK], b[MAXK];
    int i, j, l, u, v, x, y, z;

    scanf("%d%d", &n, &k);
    for (i = 0; i < n; i++) {
        scanf("%d", &a[i]);
    }
    for (i = 0; i < k; i++) {
        scanf("%d", &r[i]);
    }
    for (i = 0; i < k; i++) {
        scanf("%d", &b[i]);
    }

    memset(dp, 0x3f, sizeof(dp));
    dp[0][0][0] = 0;

    for (i = 0; i < n; i++) {
        for (j = 0; j <= k; j++) {
            for (l = 0; l <= k; l++) {
                if (dp[i][j][l] == 0x3f3f3f3f) {
                    continue;
                }
                for (u = 0; u <= k; u++) {
                    for (v = 0; v <= k; v++) {
                        x = max(j, u);
                        y = max(l, v);
                        z = max(u, v);
                        if (x + y > k || a[i] > r[u] + b[v]) {
                            continue;
                        }
                        if (x + y == k && a[i] > r[u] + b[v] + r[z] + b[z]) {
                            continue;
                        }
                        dp[i + 1][x][y] = min(dp[i + 1][x][y], dp[i][j][l] + max(0, a[i] - r[u] - b[v]));
                    }
                }
            }
        }
    }

    int ans = 0x3f3f3f3f;
    for (i = 0; i <= k; i++) {
        for (j = 0; j <= k; j++) {
            ans = min(ans, dp[n][i][j]);
        }
    }

    if (ans == 0x3f3f3f3f) {
        printf("-1\n");
    } else {
        printf("%d\n", ans);
    }

    return 0;
}
